<?phpclass Application_Model_AddonCourseItemModel extends Zend_Db_Table_Abstract {    public $_name = 'addon_course_master_item';    protected $_id = 'id';    public function getRecord($id,$acad) {        $select = $this->_db->select()                ->from($this->_name)                ->join(array('addon_master' => 'addon_course_mater'), "addon_master.id = $this->_name.addon_mater_id", array('name','code','conductedby'))                ->where("$this->_name.academic_year =?", $acad)                ->where("$this->_name.$this->_id =?", $id); //echo $select;die;        $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }public function getRecordbyname($name,$acad) {        $select = $this->_db->select()                ->from($this->_name)                ->join(array('addon_master' => 'addon_course_mater'), "addon_master.id = $this->_name.addon_mater_id", array('*'))                ->where("$this->_name.academic_year =?", $acad)                ->where("addon_master.name like ?", '%'."$name".'%');//echo "<pre>".$select;die;        $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }        public function checkExistedData($acadYear) {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.academic_year =?", $acadYear)                ->where("$this->_name.status != ?", 2);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }    public function getRecords() {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.status != ?", 2);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }    public function getAddonCourseCount($yearId) {        $select = $this->_db->select()                ->from($this->_name, array('name', 'id', 'capacity', 'conductedby', 'fee','tot_credit','code', "count(distinct(addon_allot.stu_id)) as total_applied"))                ->joinLeft(array('addon_allot' => 'addon_courses_allotment'), "addon_allot.addon_course_id = $this->_name.id", array())                ->where("$this->_name.status != ?", 2)                ->where("$this->_name.academic_year = ?", $yearId)                ->group("$this->_name.id");        //echo $select;die;        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }    public function getAddonCoursePaidCount($yearId) {        $select = $this->_db->select()                ->from('addon_courses_allotment', array("count(distinct(addon_courses_allotment.stu_id)) as total_paid", 'addon_course_id'))                ->join(array('paymode_details' => 'applicant_paymode_details'), "paymode_details.form_id = addon_courses_allotment.stu_id", array())                ->joinRight(array('addon_master' => 'addon_course_mater'), "addon_master.id = addon_courses_allotment.addon_course_id", array())                ->where("addon_master.academic_year = ?", $yearId)                ->group("addon_master.id");        $result = $this->getAdapter()                ->fetchAll($select);        //echo $select;die;        return $result;    }    public function getRecordByAddonCourse($addon_course_id) {        $select = $this->_db->select()                ->from('addon_courses_allotment', array())                ->joinLeft(array('course_d' => 'applicant_course_details'), "course_d.form_id = addon_courses_allotment.stu_id", array('course_d.applicant_name', 'course_d.form_id', 'course_d.course'))                ->joinLeft(array('deptType' => 'department_type'), "deptType.id = course_d.course", array('department_type'))                ->joinLeft(array('students' => 'erp_student_information'), "students.stu_id = course_d.form_id", array('reg_no', 'exam_roll', 'roll_no', 'stu_email_id', 'father_mobileno'))                ->where("addon_courses_allotment.addon_course_id = ?", $addon_course_id);        //echo $select;die;        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }    public function getRecordByAddonStream($addon_course_id, $stream) {        $select = $this->_db->select()                ->from('addon_courses_allotment', array())                ->joinLeft(array('course_d' => 'applicant_course_details'), "course_d.form_id = addon_courses_allotment.stu_id", array('course_d.applicant_name', 'course_d.form_id', 'course_d.course'))                ->joinLeft(array('deptType' => 'department_type'), "deptType.id = course_d.course", array('department_type'))                ->where("deptType.id = ?", $stream)                ->where("addon_courses_allotment.addon_course_id = ?", $addon_course_id);        //echo $select;die;        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }    public function getDropDownList() {        $select = $this->_db->select()                ->from($this->_name, array('id', 'name'))                //->where("status !=?",2)                ->order('name  ASC');        $result = $this->getAdapter()->fetchAll($select);        $data = array();        $st_year = '';        $end_year = '';        foreach ($result as $val) {            $data[$val['id']] = $val['name'];            // $data[$val['academic_id']] = substr($val['from_date']).'-'.substr($val['to_date']);                  }         return $data;    }             public function getDropDownListByYear($academic_year) {        $select = $this->_db->select()                ->from($this->_name, array('id', 'name'))                ->where("academic_year =?",$academic_year)                ->order('name  ASC');        $result = $this->getAdapter()->fetchAll($select);   return $result;                }public function getRecordByID($id) {             $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.id = ?", $id)                ->where("$this->_name.status != ?", 2);        $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }        public function getRecordByAcad($id,$acad) {             $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.id = ?", $id)                ->where("$this->_name.academic_year= ?", $acad)                ->where("$this->_name.status != ?", 2);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }     public function getDropDownListByYearID($acad) {        $select = $this->_db->select()                ->from($this->_name, array('id', 'name'))               ->where("$this->_name.academic_year= ?", $acad)                ->order('name  ASC');        $result = $this->getAdapter()->fetchAll($select);        $data = array();        $st_year = '';        $end_year = '';        foreach ($result as $val) {            $data[$val['id']] = $val['name'];            // $data[$val['academic_id']] = substr($val['from_date']).'-'.substr($val['to_date']);            //print_r($data);die;        }        return $data;    }}