<?phpclass Application_Model_Degree extends Zend_Db_Table_Abstract {    public $_name = 'degree_info';    protected $_id = 'id';    public function getRecord($id) {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.$this->_id =?", $id);               // ->where("status=?",0);        $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }        public function getRecords() {        $select = $this->_db->select()                ->from($this->_name);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }    public function getDegree($name) {        $select = $this->_db->select()                ->from($this->_name)                ->where('degree like ?',"$name");        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }            public function getActiveRecords() {        $select = $this->_db->select()                ->from($this->_name)                ->where('status=?',0);                    $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }            	public function getDropDownList(){        $select = $this->_db->select()		->from($this->_name, array('id','degree'))			->where("status =?",0)                        ->order('id  ASC');        $result = $this->getAdapter()->fetchAll($select);        $data = array();		$st_year ='';		$end_year='';        foreach ($result as $val) {						$data[$val['id']] = $val['degree'];			           // $data[$val['academic_id']] = substr($val['from_date']).'-'.substr($val['to_date']);			//print_r($data);die;        }        return $data;    }    	public function getDropDownListBED(){        $select = $this->_db->select()		->from($this->_name, array('id','degree'))								->where("active =?",0)                ->order('id  DESC');        $result = $this->getAdapter()->fetchAll($select);        $data = array();		$st_year ='';		$end_year='';        foreach ($result as $val) {						$data[$val['id']] = $val['degree'];			           // $data[$val['academic_id']] = substr($val['from_date']).'-'.substr($val['to_date']);			//print_r($data);die;        }        return $data;    }                }