<?phpclass Application_Model_NonCollegiateModel extends Zend_Db_Table_Abstract {    public $_name = 'non_collegiate_data';    protected $_id = 'id';    public function getRecord($id) {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.$this->_id=?", $id);                      $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }            public function getRecords() {        $select = $this->_db->select()                ->from($this->_name);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }            public function getStuRecords($uid) {        $select = $this->_db->select()                ->from($this->_name)                ->where("md5($this->_name.stu_id)=?", $uid)                 ->where("$this->_name.status=?", 0);        $result = $this->getAdapter()                ->fetchAll($select);//echo $select; exit;        return $result;    } public function getCourseRecords($session_id,$course_id,$term_id,$pay=false) {        $select = $this->_db->select()                  ->from($this->_name);                   if($pay){             $select->join(array("payment_ug"=>"ugnon_form_submission"),"payment_ug.u_id=$this->_name.stu_id",array());                 }              $select->where("$this->_name.session_id=?", $session_id);              $select->where("$this->_name.course_id=?", $course_id);               $select->where("$this->_name.term_id=?", $term_id);                                if($pay){                      $select->where("payment_ug.payment_status =?", 1);                      $select->where("payment_ug.status =?", 0);                       $select ->group("payment_ug.u_id");        }               $select ->group("$this->_name.stu_id");        $result = $this->getAdapter()                ->fetchAll($select);//echo $select; exit;        return $result;    }              }