<?phpclass Application_Model_NonPgDataModel extends Zend_Db_Table_Abstract {    public $_name = 'pg_non_form_data';    protected $_id = 'id';    public function getRecord($id) {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.$this->_id=?", $id);                      $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }            public function getRecords() {        $select = $this->_db->select()                ->from($this->_name);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }            public function getStuRecords($uid) {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.u_id=?", $uid);        $result = $this->getAdapter()                ->fetchAll($select);//echo $select; exit;        return $result;    }        public function getNonRecordbyfid($uid) {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.u_id=?", $uid)                ->where("$this->_name.status=?", 0);        $result = $this->getAdapter()                ->fetchRow($select);//echo $select; exit;        return $result;    }             public function getPayRecordbyfid($uid) {        $select = $this->_db->select()                ->from($this->_name)                ->where("md5($this->_name.u_id)=?", $uid)                ->where("$this->_name.status=?", 0);        $result = $this->getAdapter()                ->fetchRow($select);//echo $select; exit;        return $result;    }    public function getRecordbyfid($uid) {        $select = $this->_db->select()                ->from($this->_name)                ->where("md5($this->_name.u_id)=?", $uid)                ->where("$this->_name.status !=?",1)                ->order('id desc');        $result = $this->getAdapter()                ->fetchRow($select);//echo $select; exit;        return $result;    }              }