<?phpclass Application_Model_Razordb extends Zend_Db_Table_Abstract {    public $_name = 'razr_records';    protected $_id = 'rzr_id';    public function getRecordById($id){          $select = $this->_db->select()                ->from($this->_name)                  ->where('id = ?',$id);        $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }            public function getRecords(){          $select = $this->_db->select()                ->from($this->_name);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }       }