<?phpclass Application_Model_Account extends Zend_Db_Table_Abstract {    public $_name = 'account_master';    protected $_id = 'id';    public function getRecord($id) {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.$this->_id =?", $id);                     $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }        public function getRecords() {        $select = $this->_db->select()                ->from($this->_name);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }    public function getSession($name) {        $select = $this->_db->select()                ->from($this->_name)                ->where('acc_number=?',$name)                 ->where("status=?",0);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }    public function getName($name) {        $select = $this->_db->select()                ->from($this->_name)                ->where('acc_name=?',$name)                ->where("status=?",0);        $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }            public function getActiveRecords() {        $select = $this->_db->select()                ->from($this->_name)                ->where('status=?',0);                    $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }            	public function getDropDownList(){        $select = $this->_db->select()		->from($this->_name, array('id','acc_number','acc_name'))								->where("status =?",0)                ->order('id  ASC');        $result = $this->getAdapter()->fetchAll($select);        $data = array();		$st_year ='';		$end_year='';        foreach ($result as $val) {						$data[$val['id']] = $val['acc_number'].'-'.$val['acc_name'];			           // $data[$val['academic_id']] = substr($val['from_date']).'-'.substr($val['to_date']);			//print_r($data);die;        }        return $data;    }                }