<?phpclass Application_Model_AddonCourseAssignmentModel extends Zend_Db_Table_Abstract {    public $_name = 'addon_courses_allotment';    protected $_id = 'id';    public function getRecord($id) {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.$this->_id =?", $id);                     $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }        public function getRecords() {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.status != ?",2);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }            public function getaddoncourseidsRecords() {        $select = $this->_db->select()                ->from($this->_name,array("id"))                ->where("$this->_name.status != ?",2);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }   public function trashItems($stu_id='') {        $this->_db->delete($this->_name, "stu_id = '$stu_id'");    }public function getStudentRecords($stu_id){     $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.stu_id = ?",$stu_id)                ->where("$this->_name.status != ?",2);//echo $select;die;        $result = $this->getAdapter()                ->fetchAll($select);        return $result;}   public function getStudentAddonFee($stu_id){     $select = $this->_db->select()                ->from($this->_name,array("sum(addon_course_fee) as addonfee"))                ->where("$this->_name.stu_id = ?",$stu_id)                ->where("$this->_name.status != ?",2);        $result = $this->getAdapter()                ->fetchRow($select);        return $result;}         public function getStudentAddonUniqueFee($course_id){     $select = $this->_db->select()                ->from($this->_name,array("addon_course_id as course_id","count(*) as total_seat_no"))                ->where("$this->_name.addon_course_id = ?",$course_id)                ->where("$this->_name.status != ?",2)                ->group("$this->_name.addon_course_id");        $result = $this->getAdapter()                ->fetchRow($select);        return $result;}                   }