<?phpclass Application_Model_NonPgCollegiateModel extends Zend_Db_Table_Abstract {    public $_name = 'non_collegiate_stu_data';    protected $_id = 'id';    public function getRecord($id) {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.$this->_id=?", $id);                      $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }            public function getRecords() {        $select = $this->_db->select()                ->from($this->_name);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }            public function getStuRecords($uid) {        $select = $this->_db->select()                ->from($this->_name)                ->where("md5($this->_name.stu_id)=?", $uid)                ->where("$this->_name.status=?", 0);        $result = $this->getAdapter()                ->fetchAll($select);//echo $select; exit;        return $result;    }              }